import React, { Component } from 'react'
import SongListInfo from './SongListInfo';
export default class SongListPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: props.songList.info,
      songs: props.songList.songs
    };
  }
  render() {
    return (
      <div className="SongListPage">
        <div className="SongListPageInfo">
        <div className="SongListPageInfo-img">
          <img src={this.state.listImg} />
        </div>
        <div className="SongListPageInfo-info">
          <div className="SongListPageInfo-name">{this.state.listName}</div>
          {this.props.children}
          <div className="SongListPageInfo-intro">{this.state.listIntro}</div>
        </div>
      </div>
        <button>
          播放全部
        </button>
        <button title="添加全部到播放列表">+</button>
        <button>收藏</button>
        <button>分享</button>
        <button>下载全部</button>

        <SongListInfo songs={this.state.songs} listId="0001" />
      </div>
    );
  }
}
