import React, { Component } from 'react'
import SearchIcon from '../../../Icons/SearchIcon'
import PubSub from 'pubsub-js';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
class Search extends Component {
  async getSongs(value){
    value = value.slice(1).split('=')[1];
    let res = await axios(`http://localhost:4000/search?keywords=${value}`)
    console.log()
    PubSub.publish('songs',res.data.songs);
  }
  handleKeyDown = (event) =>{
    if(event.key != 'Enter') return;
    console.log('Enter');
    const value = event.target.value;
    // debug了很久的一个问题，如何在一般组件中实现跳转，
    // 使用 withRouter
    // 传递 search 参数？前面不要打斜线
    this.getSongs(value)
    this.props.history.push(`/s?q=${value}`);
  }
  render() {
    return (
      <div className='Search'>
        <SearchIcon />
        <input onKeyDown={this.handleKeyDown}></input>
      </div>
    )
  }
}
export default withRouter(Search);
