import React, { Component } from 'react'

export default class Progress extends Component {
  constructor(props) {
    super(props);
    this.state = {
      totalTime: props.totalTime,
      totalTimeMin: Math.floor(props.totalTime / 60),
      totalTimeSec: props.totalTime % 60,
      bufferTime: 0,
      curTime: 0
    };
  }
  // 想知道这么写会不会影响性能...
  render() {
    return (
      <div className="Progress">
        <span>
          {("0" + Math.floor(this.state.curTime / 60)).slice(-2)}:
          {("0" + (this.state.curTime % 60)).slice(-2)}
        </span>
        <div className="Progress-slider"></div>
        <span>
          {("0" + this.state.totalTimeMin).slice(-2)}:
          {("0" + this.state.totalTimeSec).slice(-2)}
        </span>
      </div>
    );
  }
}
