import React, { useEffect } from 'react'
import SideBar from './Component/SideBar';
import Bottom from './Component/Bottom';
import Header from './Component/Header';
import Main from './Component/Main';
import Login from './Component/Login';
import PlayerPage from './Component/PlayerPage'

import './App.css'
import { connect } from 'react-redux';
import { getLoginStateAction } from './Redux/searchActions';

import ThemeContext from './Context/ThemeContext';

function App(props) {
  const [state, setState] = React.useState({
    isPlayListShowing: false,  // 显示播放列表
    songListCurIdx: 1,         // 播放到了歌单的哪一首
    mode: 0,                   // 播放模式，0：列表循环 1：单曲循环 2：随机循环 3：顺序循环
    songSrc: '',
    loginState: null,
    showLogin:false,
  });

  useEffect(() => {
    const res = (async() => {
      return await props.getLoginState();
    })();
    console.log('res', res)
  },[]);
  const [theme, setTheme] = React.useState('light')

  /**
   * 修改主题模式
   */
  const toggleTheme = React.useCallback(() => {
    console.log('【toggleTheme】!!!!!!!!!!')
    console.log('【theme】', theme)
    const isDark = (theme == 'dark');
    console.log(isDark)
    setTheme(isDark? 'light' : 'dark')
    const bgColor = (isDark? '#fff' : '#202020');
    const btnColor = (isDark? 'rgb(255, 202, 89)' : 'blueviolet');
    document.documentElement.style.setProperty('--theme-bg-color', bgColor)
    document.documentElement.style.setProperty('--btn-color', btnColor)
    console.log(bgColor, btnColor)
  },[])

  console.log(state, setState)
  const user = localStorage.getItem('user');
  console.log(user);

  function showPlayList() {
    const { isPlayListShowing } = state;
    setState({
      isPlayListShowing: !isPlayListShowing,
    });
  }
  function showLogin(){
    setState({showLogin:true});
  }
  function changeMode(event) {

    const { mode } = state;
    setState({
      mode: (mode + 1) % 4,
    })
    event.preventDefault();
    event.stopPropagation();
  }
  function handleClick(event){
    const target = event.target.closest('.PlayListBoard');
    if (target || !state.isPlayListShowing) return;
    setState({
      isPlayListShowing: false,
    });
  }
  console.log(state.showLogin)
  // React.useState
  return (
    <div className="App" onClick={handleClick}>
      <ThemeContext.Provider value={toggleTheme}>
        {React.useMemo(() => <Header showLogin={showLogin}/>,[])}
      </ThemeContext.Provider>
      <div className="main">
      {React.useMemo(() => <Header showLogin={showLogin}/>,[])}<SideBar list={props.playlist} />
        <Main>
        </Main>
      </div>
      <Bottom
        showPlayList={showPlayList}
        changeMode={changeMode}
        mode={state.mode}
        songSrc={state.songSrc}
      />
      <PlayerPage />
      {state.showLogin && <Login></Login>}
    </div>
  );

}




// 花括号外层要包裹小括号
export default connect(
  state => ({
    loginState:state.loginState,
    detail:state.detail,
    playlist:state.playlist,
  }),
  {
    getLoginState:getLoginStateAction
  }
)(App)
