import React, { Component } from 'react'
import './index.css';
import PubSub from 'pubsub-js';
import axios from 'axios';
export default class SearchResSingers extends Component {
  state = {
    singers: null,
  }


  componentDidMount(){
    PubSub.subscribe('search',(_,result)=>{
      console.log('sub',result);
      this.setState({
        songs:result.artists,
      })
    })
  }

  timeFormat(time) {
    time /= 1000;
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  render() {
    const singers = this.state.singers || [];
    // console.log(this.state,'state');
    return (
      <div className='SearchResSingers'>
        <table>
          <thead>
            <tr>
              <td></td>
              <td></td>
              <td>音乐标题</td>
              <td>歌手</td>
              <td>专辑</td>
              <td>时长</td>
              {/* <td>热度</td> */}
            </tr>
          </thead>
          <tbody>
            {singers.length ? singers.map(s => {
              return (
                <tr key={s.id}>
                  <td></td>
                  <td></td>
                  <td>{s.name}</td>
                  <td>{s.artists[0].name}</td>
                  <td>{s.album.name}</td>
                  <td>{this.timeFormat(s.duration)}</td>
                  {/* <td>{s.artists[0].name}</td> */}
                </tr>
              )
            }) : '加载中'}
          </tbody>
        </table>
      </div>
    )
  }
}
