import React, { Component } from 'react'
import './index.css';
import axios from 'axios';
export default class SearchResSongs extends Component {
  state = {
    songs: null,
  }
  static async getDerivedStateFromProps(props){
    console.log('cat')
    let res = await axios(`http://localhost:4000/search?keywords=${JSON.stringify(props.value)}`);
    console.log(res);
    return {songs:res.data.songs};
  }
  timeFormat(time) {
    time /= 1000;
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  render() {

    const songs = this.state.songs || [];
    console.log(this.state,'state');
    return (
      <div className='SearchResSongs'>
        <table>
          <thead>
            <tr>
              <td></td>
              <td></td>
              <td>音乐标题</td>
              <td>歌手</td>
              <td>专辑</td>
              <td>时长</td>
              {/* <td>热度</td> */}
            </tr>
          </thead>
          <tbody>
            {songs.length ? songs.map(s => {
              return (
                <tr key={s.id}>
                  <td></td>
                  <td></td>
                  <td>{s.name}</td>
                  <td>{s.artists[0].name}</td>
                  <td>{s.album.name}</td>
                  <td>{this.timeFormat(s.duration)}</td>
                  {/* <td>{s.artists[0].name}</td> */}
                </tr>
              )
            }) : '加载中'}
          </tbody>
        </table>
      </div>
    )
  }
}
