import React, { Component } from 'react'
import PubSub from 'pubsub-js';

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      songList: props.songList,
      songs:null,
    };
  }
  componentDidMount(){
    PubSub.subscribe('songs',(_,songs)=>{
      console.log('sub')
      this.setState({
        songs:songs,
      })
    })
  }
  render() {
    return <div className="Main" songs={this.state.songs}>{this.props.children}</div>;
  }
}
