import React, { Component } from 'react'
import { connect } from 'react-redux'
import FullScreenIcon from '../../../Icons/FullScreenIcon';
import PauseIcon from '../../../Icons/PauseIcon';
import PlayIcon from '../../../Icons/PlayIcon';
import SoundIcon from '../../../Icons/SoundIcon';
import './index.css';

class VideoPage extends Component {
  video = React.createRef();
  state = {
    pause:false,
  }
  handlePause = () => {
    const {pause} = this.state
    if(pause) this.video.current.play();
    else this.video.current.pause();
    this.setState(state => ({
        pause:!state.pause,
    }),()=>{
      console.log('#', this.state.pause);
    })
  }
  render() {
    let url = this.props.video?.url;
    url = url?url:'';
    console.log(this.props.video);
    const {pause} = this.state;
    return (
      <div className='VideoPage'>
        <div className='video-control'>
          <div></div>
          <ul className='video-control-bar'>
            <li onClick={this.handlePause}>
              {pause?<PlayIcon/>:<PauseIcon/>}
            </li>
            <li>curDuration:totalDuration</li>
        />
            <li></li>
            <li><SoundIcon/></li>
            <li><FullScreenIcon/></li>
          </ul>
        </div>
        <video ref={this.video} src={url} autoPlay preload='auto' width="500">
        </video>
      </div>

    )
  }
}

export default connect(
  state => ({video:state.video}),
  {}
)(VideoPage)
