import axios from 'axios';
import React, { Component } from 'react'

export default class SearchResSongs extends Component {
  state = {
    songs:[],
  }

  async getSongs(s){
    console.log(s);
    let res = await axios(`https://netease-cloud-music-api-tawny-nine.vercel.app/search?keywords=${s}`);
    this.setState({
      songs:res.result.songs,
    })
  }
  render() {
    const songs = this.state.songs || [];
    return (
      <div className='SearchResSongs'>
        <table>
          <thead>
            <tr>
              <td></td>
              <td></td>
              <td>音乐标题</td>
              <td>歌手</td>
              <td>专辑</td>
              <td>时长</td>
              <td>热度</td>
            </tr>
          </thead>
          <tbody>
            {songs.length ? songs.map(s => {
              return (
                <tr key={s.id}>
                  <td></td>
                  <td>{s.name}</td>
                  <td>{s.artists}</td>
                  <td>{s.album.name}</td>
                  <td>{s.duration}</td>
                  <td>{s.artists[0].name}</td>
                  <td></td>
                </tr>
              )
            }):'加载中'}
          </tbody>
        </table>
      </div>
    )
  }
}
