import React, { Component } from 'react'
import PrevIcon from '../../../Icons/PrevIcon';
import NextIcon from '../../../Icons/NextIcon';
import PauseIcon from '../../../Icons/PauseIcon';
import Progress from '../Progress';
import ModeIcon1 from '../../../Icons/ModeIcon1';
import ModeIcon2 from '../../../Icons/ModeIcon2';
import ModeIcon3 from '../../../Icons/ModeIcon3';
import ModeIcon4 from '../../../Icons/ModeIcon4';
import PlayIcon from '../../../Icons/PlayIcon';
import PubSub from 'pubsub-js';

export default class Player extends Component {
  constructor(props) {
    super(props);
    this.state = {
      curTime: 0,
      totalTime: 0,
      pause: true,
      isHandling: false,
    };
  }
  // 切歌 
  handleChange(num) {
    this.props.changeSong(num);
  }
  // 播放 / 暂停
  handlePause = (hasSourse) => {
    if (!hasSourse) return;
    const { pause } = this.state;
    if (pause) this.audio.play();
    else this.audio.pause();
    this.setState({
      pause: !pause,
    })
  }
  // 监听播放器进度变化
  handleTimeUpdate = (event) => {
    // cur 传给子组件使用
    const cur = Math.floor(event.target.currentTime);
    const { isHandling } = this.state;
    if (isHandling) return;
    this.setState({
      curTime: cur,
    })
    // 如果正在调整播放进度，则不改变进度条的长度
    const width = Math.floor(cur / event.target.duration * 350) || 0;
    console.log('width', width)
    // 修改 css 变量 感觉会影响性能
    document.documentElement.style.setProperty('--progress-bar-width', width + 'px');
  }
  // 用户改变 audio 播放进度
  handleProgressChange = (newProgress) => {
    this.audio.currentTime = newProgress;
  }

  handleMouseDown = (event) => {
    // ?
    if (this.state.isHandling) return;
    // 是否是点击事件
    let isClick = false;
    
    const that = this;
    const { time: totalTime } = this.props;
    console.log(totalTime)
    // top 的 coord
    const top = event.target;
    const topCoord = top.getBoundingClientRect();
    // 整个进度条
    const bar = top.closest('.Progress-slider');
    const barCoord = bar.getBoundingClientRect();
    // 鼠标指针点击位置与 top 左上角的 offset
    const start = {
      x:event.clientX,
      y:event.clientY
    }
    const offset = {
      x: event.clientX - topCoord.left,
      y: event.clientY - topCoord.top,
    }

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    function handleMouseMove(event) {
      console.log(event.clientX - start.x);
      if(!isClick && Math.floor(event.clientX - start.x) != 0){
        isClick = true;
        that.setState({
          isHandling:true,
        })
      }
      let width = event.clientX - barCoord.left - offset.x;
      width = width < 350 ? width : 350;
      width = width > 0 ? width : 0;
      that.setState({
        curTime: Math.floor(width / 350 * totalTime),
      })
      document.documentElement.style.setProperty('--progress-bar-width', width + 'px');
    }
    function handleMouseUp(event) {
      // 发生了鼠标点击事件
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);

      if (!isClick) {
        console.log('click');
        const target = event.target;
        const progressBarCoord = target.getBoundingClientRect();
        const width = event.clientX - progressBarCoord.left;
        this.changeProgress(width);
      }
      that.setState({
        isHandling: false,
      })
      const width = event.clientX - barCoord.left - offset.x;
      console.log('handleMouseUp', width);
      if (!event.target.closest('.Progress-slider')) bar.classList.remove('Progress-slider-hover');
      that.handleProgressChange(width);
    }
  }

  componentDidMount() {
    PubSub.subscribe('VolumeChange', ((_, volume) => {
      this.audio.volume = volume;
    }))
  }
  render() {
    const hasSourse = (this.props.songSrc == '') ? false : true;
    const { mode, changeMode, time: totalTime, songSrc } = this.props;
    const { curTime, isHandling } = this.state;
    console.log('render', isHandling)
    const modeIcon = (mode === 0) ?
      <ModeIcon1 /> : (mode === 1) ?
        <ModeIcon2 /> : (mode === 2) ?
          <ModeIcon3 /> : <ModeIcon4 />
    return (
      <div className="Player">
        <div className="Player-btns">
          <div className="Player-mode" onClick={changeMode}>
            {modeIcon}
          </div>
          <ul>
            <li className="Player-btn" onClick={() => this.handleChange(-1)} title="上一首">
              <PrevIcon />
            </li>
            <li className="Player-btn play-icon" title="暂停" onClick={() => this.handlePause(hasSourse)}>
              {this.state.pause ? <PlayIcon /> : <PauseIcon />}
            </li>
            <li className="Player-btn" onClick={() => this.handleChange(1)} title="下一首">
              <NextIcon />
            </li>
          </ul>
        </div>
        <Progress
          totalTime={totalTime}
          curTime={curTime}
          className="Player-progress"
          handleProgressChange={this.handleProgressChange}
          handleMouseDown={this.handleMouseDown}
          isHandling={isHandling}
        />
        <audio
          ref={c => this.audio = c}
          src={songSrc}
          onTimeUpdate={this.handleTimeUpdate}
        >Your browser can't support HTML5 Audio</audio>
      </div>
    );
  }
}
