import React, { Component } from 'react'
import './index.css';
import PubSub from 'pubsub-js';
import store from '../../../../Redux/store';
import axios from 'axios';
export default class SearchResSingers extends Component {
  state = {
    artists: null,
  }

  componentDidMount(){
    // PubSub.subscribe('search',(_,result)=>{
    //   console.log('sub',result);
    //   this.setState({
    //     artists:result.artists,
    //   })
    // })
  }

  timeFormat(time) {
    time /= 1000;
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  render() {

    let {artists} = store.getState();
    artists = artists || [];
    console.log('ResArtists',artists);
    return (
      <div className='SearchResArtists'>
        <table>
          <tbody>
            {artists.length ? artists.map(a => {
              return (
                <tr key={a.id}>
                  <td className='artistAvatar'>
                    <img src={a.img1v1Url}></img>
                  </td>
                  <td>{a.name}</td>
                  
                </tr>
              )
            }) : <tr>加载中</tr>}
          </tbody>
        </table>
      </div>
    )
  }
}
