import React, { Component } from 'react'

export default class PlayListBoard extends Component {
  timeFormat(time) {
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  render() {
    const { songs } = this.props.list;
    return (
      <div className='PlayListBoard'>
        <div className="PlayListBoard-header">
          <h1>当前播放</h1>
          <div className="PlayListBoard-info">
            <span>总100首</span>
            <span className='PlayListBoard-info-btns'>
              <button>收藏全部</button>
              <button>清空列表</button>
            </span>
          </div>
        </div>
        <div className='PlayListBoard-main'>
          <table>
            <tbody>
              {songs.map((s, idx) => {
                console.log(s);
                return <tr>
                  <td>{s.name.length > 29 ? (s.name.substr(0, 28) + '...') : s.name}</td>
                  <td>{s.singer.length > 13 ? (s.singer.subStr(0, 12) + '...') : s.singer}</td>
                  <td>{this.timeFormat(s.time)}</td>
                </tr>
              })}
            </tbody>
          </table>
        </div>

      </div>
    )
  }
}
