import React, { Component } from 'react'
import SongList from '../SongList';
import Comments from '../Comments';
import { Switch, Route, NavLink } from 'react-router-dom';
export default class SongListInfo extends Component {
  static tab = ["歌曲列表", "评论"];
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 0,
      songs: props.songs
    };
  }
  render() {
    const { listId, songs } = this.props;
    let tab = SongListPageDetail.tab.map((t, idx) => {
      if (idx == this.state.selectedTab) {
        return (
          <li className="selected" key={"tab" + idx}>
            {t}
          </li>
        );
      } else return <li key={"tab" + idx}>{t}</li>;
    });
    return (
      <div className="SongListPageDetail">
        <ul className="SongListPage-tab">
            <NavLink to={{pathname:`/${listId}/list`,state:{songs}}}>歌曲列表</NavLink>
            <NavLink to={{pathname:`/${listId}/comments`,state:{comments}}}>评论</NavLink>
        </ul>
        <Switch>
          <Route path={`/${listId}/list`} component={SongList}></Route>
          <Route path={`/${listId}/comments`} component={Comment}></Route>
        </Switch>
      </div >
    );
  }
}
