import React, { Component } from 'react'
import { NavLink, Switch, Route } from 'react-router-dom'
import axios from 'axios';
import PubSub from 'pubsub-js';
import './index.css';
import SearchResSongs from './SearchResSongs';
// 1: 单曲, 10: 专辑, 100: 歌手, 1000: 歌单, 1002: 用户, 1004: MV, 1006: 歌词, 1009: 电台, 1014: 视频,
const TYPE = {
  'songs':1,
  'singers':100,
  'albums':10,
  'vedios':1014,
  'songlists':1000,
  'lyrics':10,
  'users':1002,
}
export default class SearchResPage extends Component {
  state = {
    searchData:'Rick and Morty',
  }

  async getSongs(value, type){
    let res = await axios(`http://localhost:4000/search?keywords=${value}&&type=${type}`)
    console.log(res);
    PubSub.publish('search',res.data.result.songs);
  }

  render() {
    console.log('page',this.props)
    const searchData = this.props.location.search.split('=')[1];
    const type = this.props.location.pathname.slice(3);
    this.getSongs(searchData, TYPE[type]);
    return (
      <div className='SearchResPage'>
        <h1>搜索</h1>
        <ul className='Search-Navbar'>
          <li><NavLink to={`/s/songs?search=${searchData}`} className='active'>单曲</NavLink></li>
          <li><NavLink to={`/s/singers?search=${searchData}`}>歌手</NavLink></li>
          <li><NavLink to={`/s/albums?search=${searchData}`}>专辑</NavLink></li>
          <li><NavLink to={`/s/vedios?search=${searchData}`}>视频</NavLink></li>
          <li><NavLink to={`/s/songlists?search=${searchData}`}>歌单</NavLink></li>
          <li><NavLink to={`/s/lyrics?search=${searchData}`}>歌词</NavLink></li>
          <li><NavLink to={`/s/users?search=${searchData}`}>用户</NavLink></li>
        </ul>
        <Switch>
          <Route path='/s/songs' component={SearchResSongs}></Route>
          <Route path='/s/singers' component={SearchResSongs}></Route>
          <Route path='/ss' component={SearchResSongs}></Route>
          <Route path='/s/songs' component={SearchResSongs}></Route>
          <Route path='/s/songs' component={SearchResSongs}></Route>
          <Route path='/s/songs' component={SearchResSongs}></Route>
          <Route path='/s/songs' component={SearchResSongs}></Route>

        </Switch>
        {/* <SearchResSongs /> */}
      </div>
    )
  }
}
