import React, { Component } from 'react'
import Liked from '../../../../Liked';
export default class Songs extends Component {
  timeFormat(time) {
    time = Math.floor(time / 1000);
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  render() {
    console.log("Songs", this.props);
    const { songs } = this.props;
    return (
      <table className="SongListPageSongs">
        <thead>
          <tr>
            <th></th>
            <th>操作</th>
            <th>标题</th>
            <th>歌手</th>
            <th>专辑</th>
            <th>时间</th>
          </tr>
        </thead>
        <tbody>{
          songs && songs.map((s, idx) => (
            <tr key={idx}>
              <td className='list-idx'>{((idx < 9)? "0": "") + (idx + 1)}</td>
              <td>
                <Liked like={s.like} />
              </td>
              <td className='list-name'>{s.name}</td>
              <td className='list-artist'>{s.ar.reduce((res, a) => (res == ''? '' :' / ') + a.name,'')}</td>
              <td className='list-album'>{s.al.name}</td>
              <td className='list-time'>{this.timeFormat(s.dt)}</td>
            </tr>
          ))
        }</tbody>
      </table>
    );
  }
}
