import React, { Component } from 'react'
import './index.css';
export default class Progress extends Component {
  isHandling = false;

  handleProgressChange = (width) => {
    const newProgress = Math.floor(width / 350 * this.props.totalTime);
    this.props.handleProgressChange(newProgress);
  }

  handleMouseEnter = (event) => {
    const that = this;
    const target = event.target.closest('.Progress-slider');
    target.classList.add('Progress-slider-hover');

    target.addEventListener('mouseleave', handleMouseLeave);

    function handleMouseLeave(event){
      if(!that.isHandling){
        target.classList.remove('Progress-slider-hover');
        target.removeEventListener('mouseleave', handleMouseLeave);
      }
    }
  }
  // 点击操控 Progress
  handleClick = (event) => {
    console.log('click');
    const target = event.target;
    const progressBarCoord = target.getBoundingClientRect();
    const width = event.clientX - progressBarCoord.left;
    this.handleProgressChange(width);
  }

  handleMouseDown = (event) => {
    console.log('mousedown');

    if(this.isHandling) return;
    this.isHandling = true;
    const that = this;

    // top 的 coord
    const topCoord = event.target.getBoundingClientRect();
    // 整个进度条
    const bar = event.target.closest('.Progress-slider');
    const barCoord = bar.getBoundingClientRect();
    // 鼠标指针点击位置与 top 左上角的 offset
    const offset = {
      x:event.clientX - topCoord.left,
      y:event.clientY - topCoord.top,
    }

    document.addEventListener('mousemove',handleMouseMove);
    document.addEventListener('mouseup',handleMouseUp);

    function handleMouseMove(event){
      const width = event.clientX - barCoord.left - offset.x;
      document.documentElement.style.setProperty('--progress-bar-width', width + 'px');
    }
    function handleMouseUp(event){
      const width = event.clientX - barCoord.left - offset.x;
      document.removeEventListener('mousemove',handleMouseMove);
      document.removeEventListener('mouseup',handleMouseUp);
      that.isHandling = false;
      if(event.target.closest('.Progress-slider'))bar.classList.remove('Progress-slider-hover');
      that.handleProgressChange(width);
    }
  }
  // 想知道这么写会不会影响性能...

  render() {
    const {totalTime, curTime} = this.props;
    const totalTimeMin = Math.floor(totalTime / 60),
          totalTimeSec = totalTime % 60,
          bufferTime = 0;
    return (
      <div className="Progress">
        <span>
          {("0" + Math.floor(curTime / 60)).slice(-2)}:
          {("0" + (curTime % 60)).slice(-2)}
        </span>

        <div className="Progress-slider" onMouseEnter={this.handleMouseEnter}  onClick={this.handleClick}>
          <div className='Progress-slider-bar'>
            <div className='Progress-slider-top' onMouseDown={this.handleMouseDown}></div>
          </div>
        </div>
        <span>
          {("0" + totalTimeMin).slice(-2)}:
          {("0" + totalTimeSec).slice(-2)}
        </span>
      </div>
    );
  }
}
