import React, { Component } from 'react'
import './index.css';
import PubSub from 'pubsub-js';
import store from '../../../../Redux/store';
import axios from 'axios';
export default class SearchResSongs extends Component {
  state = {
    songs: null,
  }
  componentDidMount(){
    PubSub.subscribe('search',(_,result)=>{
      console.log('sub',result);
      this.setState({
        songs:result.songs,
      })
    })
  }

  timeFormat(time) {
    time /= 1000;
    let min = Math.floor(time / 60),
      sec = time % 60;
    return ("0" + min).slice(-2) + ":" + ("0" + sec).slice(-2);
  }
  addSongToList = (e) => {
    const target = e.target.closest('tr');
    console.log(target);
    const id = target.key;
    console.log(id);
  }
  render() {
    let {songs} = store.getState()
    songs = songs || [];
    console.log('ResSongs',songs);
    return (
      <div className='SearchResSongs'>
        <table>
          <thead>
            <tr>
              <td></td>
              <td></td>
              <td>音乐标题</td>
              <td>歌手</td>
              <td>专辑</td>
              <td>时长</td>
            </tr>
          </thead>
          <tbody onDblClick={this.addSongToList}>
            {songs.length ? songs.map((s, idx) => {
              
              return (
                <tr key={s.id}>
                  <td className='list-idx'>{(idx+1 < 10)? '0' + (idx+1) : idx+1}</td>
                  <td></td>
                  <td className='list-name'>{s.name}</td>
                  <td>{s.artists[0].name}</td>
                  <td>{s.album.name}</td>
                  <td className='list-time'>{this.timeFormat(s.duration)}</td>
                </tr>
              )
            }) : <tr>加载中</tr>}
          </tbody>
        </table>
      </div>
    )
  }
}
