import './index.css';
export default class Progress extends Component {

  handleClick = (event) => {
    const target = event.target;
    const progressBarCoord = target.getBoundingClientRect();
    const width = event.clientX - progressBarCoord.left;
    const newProgress = width / 350 
    this.props.handleProgressChange();
  }
  // 想知道这么写会不会影响性能...

  render() {
    const {totalTime, curTime} = this.props;
    const totalTimeMin = Math.floor(totalTime / 60),
          totalTimeSec = totalTime % 60,
          bufferTime = 0;
    return (
      <div className="Progress">
        <span>
          {("0" + Math.floor(curTime / 60)).slice(-2)}:
          {("0" + (curTime % 60)).slice(-2)}
        </span>

        <div className="Progress-slider" onClick={this.handleClick}>
          <div className='Progress-slider-bar'></div>
        </div>
        <span>
          {("0" + totalTimeMin).slice(-2)}:
          {("0" + totalTimeSec).slice(-2)}
        </span>
      </div>
    );
  }
}
