  componentDidMount() {

  }
  handleClick = (event) => {
    console.log(event);
    const target = event.target;
    const top = target.closest('.soundController-bar-top');
      // if(isTop) return;
    // const ControllerTopCoord = this.topItem.getBoundingClientRect();
    const ctrCoord = this.ctr.getBoundingClientRect();
 
    const targetCoord = {
      x:event.pageX - ctrCoord.x,
      y:event.pageY
    }
    console.log(targetCoord); 

  }
  handleVolumeChange(volume) {
    PubSub.publish('volumeChange', volume);
  }
  render() {
    return (
      <ul className='Func'>
        <li className='Sound'>
          <SoundIcon></SoundIcon>
          <div ref={c => this.ctr = c} className='soundController' onClick={this.handleClick}>
            <div className='soundController-bar-bg'></div>
            <div className='soundController-bar'>
              <div className='soundController-bar-body'></div>
              <divc lassName='soundController-bar-top'></divc>
            </div>

          </div>
        </li>
        <li className='PlayList' onClick={this.props.showPlayList}><PlayListIcon></PlayListIcon></li>

      </ul>
    )
  }
}
