import React, { Component } from 'react'
import './index.css';
export default class Progress extends Component {

  handleProgressChange = (width) => {
    const newProgress = Math.floor(width / 350 * this.props.totalTime);
    this.props.handleProgressChange(newProgress);
  }
  handleClick = (event) => {
    const target = event.target;
    const progressBarCoord = target.getBoundingClientRect();
    const width = event.clientX - progressBarCoord.left;
    this.handleProgressChange(width);
  }
  handleMouseDown(event){
    const coord = event.target.getBoundingClientRect();
    const offset = {
      x:event.clientX - coord.left,
      y:event.clientY - coord.top,
    }
    console.log(offset)
    document.addEventListener('mouseMove',handleMouseMove);
    document.addEventListener('mouseUp',handleMouseUp);

    handleMouseMove = (event) => {
      const width = event.clientX - coord.left - offset.x;
      document.documentElement.style.setProperty('--progress-bar-width', width + 'px');
      console.log(width);
    }
    handleMouseUp = (event) => {

    }
  }
  // 想知道这么写会不会影响性能...

  render() {
    const {totalTime, curTime} = this.props;
    const totalTimeMin = Math.floor(totalTime / 60),
          totalTimeSec = totalTime % 60,
          bufferTime = 0;
    return (
      <div className="Progress">
        <span>
          {("0" + Math.floor(curTime / 60)).slice(-2)}:
          {("0" + (curTime % 60)).slice(-2)}
        </span>

        <div className="Progress-slider" onClick={this.handleClick}>
          <div className='Progress-slider-bar'>
            <div className='Progress-slider-top' onMouseDown={this.handleMouseDown}></div>
          </div>
        </div>
        <span>
          {("0" + totalTimeMin).slice(-2)}:
          {("0" + totalTimeSec).slice(-2)}
        </span>
      </div>
    );
  }
}
