import React, { Component } from 'react'
import { NavLink } from 'react-router-dom'
import axios from 'axios';
import './index.css';
import SearchResSongs from './SearchResSongs';
export default class SearchResPage extends Component {
  state = {
    songs:null,
  }
  // render 方法不能为异步的
  handleSearchValue(str){
    let arr = str.split('=');
    return arr[1];
  }
  async getSongs(value){
    let res = await axios(`http://localhost:4000/search?keywords=${JSON.stringify(value)}`);
    this.setState({
      songs:res.data.songs,
    })
  }
  static async getDerivedStateFromProps(props){
    const value = this.handleSearchValue(this.props.location.search.slice(1));
    getSongs(value);
  }
  render() {
    return (
      <div className='SearchResPage'>
        <h1>搜索</h1>
        <ul className='Search-Navbar'>
          <li><NavLink to='/s/songs' className='active'>单曲</NavLink></li>
          <li><NavLink to='/s/singers'>歌手</NavLink></li>
          <li><NavLink to='/s/albums'>专辑</NavLink></li>
          <li><NavLink to='/s/vedios'>视频</NavLink></li>
          <li><NavLink to='/s/songlists'>歌单</NavLink></li>
          <li><NavLink to='/s/lyrics'>歌词</NavLink></li>
          <li><NavLink to='/s/users'>用户</NavLink></li>
        </ul>
        <SearchResSongs songs={this.state.songs}/>
      </div>
    )
  }
}
