import { getLyricAction } from "./songActions"

export const toggleSongPageAction = (id, open) => {
  return {
    type: 'toggleSongPage',
    data: {
      // 获取歌词
      lyric: getLyricAction(id),
      songPageOpen:!preState
    }
  }
}
