import React, { Component } from 'react'
import Songs from './Songs';
import { NavLink, Route, Switch, Redirect } from 'react-router-dom';
import Comments from './Comments';


export default class SongListPage extends Component {
  data = {
    songList: [{
      listName: "夜凉不寐，月色如水",
      path: '/0002',
      id: '0002',
      info: {
        name: "未命名",
        intro: "简介",
        imgSrc:
          "https://img0.baidu.com/it/u=2928837390,1445198898&fm=253&fmt=auto&app=138&f=JPEG?w=500&h=500"
      },
      songs: [
        {
          name: "Crying",
          singer: "Still Corners",
          album: "Crying",
          time: 208
        },
        {
          name: "Sea, Swallow Me",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 189
        },
        {
          name: "The Ghost Has No Home",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 455
        },
        {
          name: "堕落",
          singer: "王菲",
          album: "浮躁",
          time: 222
        }
      ],
      comments: [
        {
          username: '孜然',
          avaturUrl:'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimglf5.nosdn0.126.net%2Fimg%2FZUVRSDF6U2FLSm9UQ1VINHRlRkxTNGVhM3R5SGJ5RWg0NVlKQkpMdHdCZU1TVjNRdEU4dDF3PT0.jpg%3FimageView%26thumbnail%3D500x0%26quality%3D96%26stripmeta%3D0%26type%3Djpg&refer=http%3A%2F%2Fimglf5.nosdn0.126.net&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1649657796&t=8459925c8fed98ed69fe47bed0f503e5',
          comment: 'good',
        }
      ]
    }, {
      listName: "我喜欢的音乐",
      path: '/0001',
      id: '0001',
      info: {
        name: "未命名",
        intro: "简介",
        imgSrc:
          "https://y.qq.com/music/photo_new/T002R300x300M000001mZMtj15CO0z_2.jpg?max_age=2592000",
      },
      songs: [
        {
          name: "Sea, Swallow Me",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 189
        },
        {
          name: "The Ghost Has No Home",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 455
        },
      ],
      comments: [
        {
          username: '孜然',
          avaturUrl:'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimglf5.nosdn0.126.net%2Fimg%2FZUVRSDF6U2FLSm9UQ1VINHRlRkxTNGVhM3R5SGJ5RWg0NVlKQkpMdHdCZU1TVjNRdEU4dDF3PT0.jpg%3FimageView%26thumbnail%3D500x0%26quality%3D96%26stripmeta%3D0%26type%3Djpg&refer=http%3A%2F%2Fimglf5.nosdn0.126.net&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1649657796&t=8459925c8fed98ed69fe47bed0f503e5',
          comment: 'not bad',
        }
      ]
    }]

  }
  render() {
    const listId = this.props.match.params.listid;//使用这个id向服务器请求数据
    console.log(this.props.match.params)
    const list = this.data.songList.find((sl) => {
      return sl.id === listId;
    })
    const {
      info: { imgSrc, intro },
      listName,
      songs,
      id,
      comments
    } = list;
    return (
      <div className="SongListPage">
        <div className="SongListPageInfo">
          <div className="SongListPageInfo-img">
            <img src={imgSrc} />
            <div className='BigHeart'>
            <svg t="1648190679462" class="icon" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="1945" width="200" height="200"><path d="M981.714286 250.971429a297.188571 297.188571 0 0 0-65.028572-94.628572 302.171429 302.171429 0 0 0-96-63.428571A303.245714 303.245714 0 0 0 703.657143 69.714286c-56.342857 0-111.314286 15.428571-159.085714 44.571428-11.428571 6.971429-22.285714 14.628571-32.571429 22.971429-10.285714-8.342857-21.142857-16-32.571429-22.971429-47.771429-29.142857-102.742857-44.571429-159.085714-44.571428-40.571429 0-79.885714 7.771429-117.028571 23.2-35.885714 14.857143-68.228571 36.228571-96 63.428571a295.36 295.36 0 0 0-65.028572 94.628572c-15.885714 36.914286-24 76.114286-24 116.457142 0 38.057143 7.771429 77.714286 23.2 118.057143 12.914286 33.714286 31.428571 68.685714 55.085715 104 37.485714 55.885714 89.028571 114.171429 153.028571 173.257143 106.057143 97.942857 211.085714 165.6 215.542857 168.342857l27.085714 17.371429c12 7.657143 27.428571 7.657143 39.428572 0l27.085714-17.371429c4.457143-2.857143 109.371429-70.4 215.542857-168.342857 64-59.085714 115.542857-117.371429 153.028572-173.257143 23.657143-35.314286 42.285714-70.285714 55.085714-104 15.428571-40.342857 23.2-80 23.2-118.057143 0.114286-40.342857-8-79.542857-23.885714-116.457142z" p-id="1946" fill="#ffffff"></path></svg>
            </div>
          </div>
          <div className="SongListPageInfo-info">
            <div className="SongListPageInfo-name">{listName}</div>
            <div className="SongListPageFunc">
              <button>播放全部</button>
              <button title="添加全部到播放列表">+</button>
              <button>收藏</button>
              <button>分享</button>
              <button>下载全部</button>
            </div>
            <div className="SongListPageInfo-intro">{intro}</div>
          </div>
        </div>
        {/* <SongListInfo songs={songs} listId="0001" /> */}
        <div className="SongListPageDetail">
          <ul className="SongListPage-tab">
            <li><NavLink to={{ pathname: `/${id}/list`, state: { songs } }}>歌曲列表</NavLink></li>
            <li><NavLink to={{ pathname: `/${id}/comments`, state: { comments } }}>评论</NavLink></li>
          </ul>
          <Switch>
            <Route path={`/${id}/list`} component={Songs}></Route>
            <Route path={`/${id}/comments`} component={Comments}></Route>
          </Switch>
        </div >

        <Songs songs={songs}></Songs>
      </div>
    );
  }
}
