import React, { Component } from 'react'
import PrevIcon from '../../../Icons/PrevIcon';
import NextIcon from '../../../Icons/NextIcon';
import PauseIcon from '../../../Icons/PauseIcon';
import Progress from '../Progress';
export default class Player extends Component {
  constructor(props) {
    super(props);
    this.state = {
      totalTime: this.props.time
    };
  }
  handleChange(num){
    this.props.changeSong(num);
  }
  render() {
    return (
      <div className="Player">
        <div className="Player-btns">
          <div className="Player-mode">
          <svg t="1648279919767" class="icon" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="2365" width="200" height="200"><path d="M938.61 570.44c-10.7 0-19.82 8.18-20.52 18.86C908.35 740 782.63 859.64 629.5 859.64H402.48c-156.61 0-290.18-121.55-296.09-278.05-6.22-164.53 125.82-300.36 288.99-300.36H533.1v-0.01h165.04v100.5c0 13.14 14.36 21.23 25.6 14.43L924 275c10.85-6.56 10.85-22.3 0-28.86L723.74 124.98c-11.24-6.8-25.6 1.29-25.6 14.43v100.5H489.79v0.01h-86.96c-185.3 0-341.42 151.64-337.9 336.91 3.41 179.59 150.05 324.13 330.46 324.13H629.5c175.07 0 318.35-136.11 329.78-308.29 0.8-11.99-8.65-22.23-20.67-22.23z" p-id="2366" fill="#515151"></path><path d="M550 708V456.66h-25.92l-67.45 54.67 17.75 21.65L519.47 495h0.71v213z" p-id="2367" fill="#515151"></path></svg>
          </div>
          <ul>
            <li className="Player-btn" onClick={()=>this.handleChange(-1)} title="上一首">
              <PrevIcon />
            </li>
            <li className="Player-btn" title="暂停">
              <PauseIcon />
            </li>
            <li className="Player-btn"  onClick={()=>this.handleChange(1)}  title="下一首">
              <NextIcon />
            </li>
          </ul>
        </div>
        <Progress
          totalTime={this.state.totalTime}
          className="Player-progress"
        />
      </div>
    );
  }
}
