import React, { useEffect } from 'react'
import './index.css';
const imgNums = 4;
export default function Carousel(props) {
  const [curIdx, setCurIdx ] = React.useState(0)

  useEffect(()=>{
    // 请求轮播图
    let res = (async() => await 1)();
    let timer = setInterval(()=> {
      setCurIdx((idx) => (idx + 1 > imgNums - 1)? 0 : idx + 1)
    },3000)
    return ()=>{
      clearTimeout(timer);
    }
  },[])
  console.log(curIdx)

  const {imgs = ['a','b','c','d']} = props;
  return (
    <div>
      <div className='carousel'>
        {imgs.map((item, idx) => <div key={idx} level={((curIdx - idx)%4 === 1)? 'sub-left' : ((idx - curIdx === 1)? 'sub-right' : ((curIdx === idx)? 'top' : 'hide'))}>{item}</div>)}
      </div>
      <ul className='carousel-label'>
        {imgs.map((item, idx) => <li key={idx} className={(idx == curIdx)? 'active' : ''} ></li>)}
      </ul>
    </div>
  )
}
