import React from 'react'
import { connect } from 'react-redux'
import { getLyricAction } from '../../Redux/songActions'
import './index.css'

function PlayerPage(props) {
  const {playerPageOpen = false} = props;
  console.log("playerPageOpen", playerPageOpen)
  return (
    playerPageOpen &&
    <div className='PlayerPage'>
      <div className='player-cover'>
        <img src=""></img>
      </div>
      <div className='player-song-name'></div>
      
      <div className='player-lyric'>
        {props.lyric.split('\n')}
      </div>
    </div>
  )
}


export default connect(
  (state)=>({
    playerPageOpen:state.playerPageOpen,
    lyric:state.lyric
  }),
  {
    getLyric:getLyricAction
  }
)(PlayerPage)
