import React, { Component } from 'react'
import SongListInfo from './SongListInfo';
import Songs from './Songs';
export default class SongListPage extends Component {
  data = {
    songList:{
      listName: "夜凉不寐，月色如水",
      path: '/0002',
      id: '0002',
      info: {
        name: "未命名",
        intro: "简介",
        imgSrc:
          "https://img0.baidu.com/it/u=2928837390,1445198898&fm=253&fmt=auto&app=138&f=JPEG?w=500&h=500"
      },
      songs: [
        {
          name: "Crying",
          singer: "Still Corners",
          album: "Crying",
          time: 208
        },
        {
          name: "Sea, Swallow Me",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 189
        },
        {
          name: "The Ghost Has No Home",
          singer: "Cocteau Twins / Harold Budd",
          album: "The Moon and the Melodies",
          time: 455
        },
        {
          name: "堕落",
          singer: "王菲",
          album: "浮躁",
          time: 222
        }
      ]

    }
  }
  render() {
    console.log(this.props.match.params);//使用这个id向服务器请求数据
    const {info:{imgSrc, intro}, listName,songs} = this.data.songList;
    return (
      <div className="SongListPage">
        <div className="SongListPageInfo">
            <div className="SongListPageInfo-img">
              <img src={imgSrc} />
            </div>
            <div className="SongListPageInfo-info">
                <div className="SongListPageInfo-name">{listName}</div>
                <div className="SongListPageFunc">
                    <button>播放全部</button>
                <button title="添加全部到播放列表">+</button>
                <button>收藏</button>
                <button>分享</button>
                <button>下载全部</button>
            </div>
            <div className="SongListPageInfo-intro">{intro}</div>
          </div>
        </div>
        <SongListInfo songs={songs} listId="0001" />
        <div className="SongListPageDetail">
        <ul className="SongListPage-tab">
            <li><NavLink to={{pathname:`/${listId}/list`,state:{songs}}}>歌曲列表</NavLink></li>
            <li><NavLink to={{pathname:`/${listId}/comments`,state:{comments}}}>评论</NavLink></li>
        </ul>
        <Switch>
          <Route path={`/${listId}/list`} component={SongList}></Route>
          <Route path={`/${listId}/comments`} component={Comment}></Route>
        </Switch>
        </div >
        <Songs songs = {songs}></Songs>
      </div>
    );
  }
}
