import React, { Component } from 'react'
import SideBar from './Component/SideBar';
import Bottom from './Component/Bottom';
import Header from './Component/Header';
import Main from './Component/Main';
import SongListPage from './Component/Main/SongListPage';
import './App.css'
import { Router, Route, Switch, Redirect} from 'react-router-dom';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedList: 0,
      song : {
        name: "Sea, Swallow Me",
        singer: "Cocteau Twins / Harold Budd",
        imageSrc:
          "https://y.qq.com/music/photo_new/T002R300x300M000001mZMtj15CO0z_2.jpg?max_age=2592000",
        time: 189
      },

      list:[{
        listName:"我喜欢的音乐",
        path:'/0001',
        id:'0001',
      }, {
        listName:"夜凉不寐，月色如水",
        path:'/0002',
        id:'0002',

      }]
    };
  }

  render() {
    return (
      <div className="App">
        <Header />
        <div className="main">

          <SideBar list={this.state.list} selectedList={this.state.selectedList} />

          <Main songList={this.state.songList} >
            <Switch>
              <Route path={`/songlist/:listid`} component={SongListPage}  songList={this.state.songList}></Route>
              <Redirect to='/songlist/0001'></Redirect>
            </Switch>
          </Main>
        </div>
        <Bottom song={this.state.song} />
      </div>
    );
  }
}
