import React from 'react'
import './index.css'
import ThemeContext from '../../../Context/ThemeContext';

export default function ModeSwitch(props) {
  const switchInput = React.createRef(null); 
  function changeTheme(){
    props.changeTheme()
    console.log('【switchInput.current.checked】', switchInput.current.checked)
  }
  return (
    <ThemeContext.Consumer>
      { theme => (
        <label for='switch' onClick={changeTheme}>
          <input type="checkbox" id="switch" ref={switchInput}></input>
          <div className="ModeSwitch"></div>
      </label>
      )}
    </ThemeContext.Consumer>
  )
}
